CREATE TABLE `user` (`id` int(11) NOT NULL AUTO_INCREMENT COMMENT '系统用户标识',`roles` varchar(500) NOT NULL COMMENT '系统用户所属角色集合(role_id以英文逗号分隔)',`account` varchar(64) NOT NULL COMMENT '系统用户账号',`password` varchar(64) NOT NULL COMMENT '系统用户密码',`salt` varchar(50) NOT NULL COMMENT '加盐',`name` varchar(50) NOT NULL COMMENT '系统用户姓名',`email` varchar(64) NOT NULL COMMENT '系统用户电子邮箱',`telephone` varchar(36) NOT NULL COMMENT '系统用户用户电话号码,多个用英文逗号分开',`status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '系统用户的状态,1表示启用,0表示禁用,默认为1,其他保留',`comment` varchar(50) NOT NULL COMMENT '系统用户备注',`gmt_created` timestamp NOT NULL DEFAULT '2014-01-01 01:01:01' COMMENT '系统用户记录创建时间',`gmt_modified` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '系统用户记录更新时间戳',PRIMARY KEY (`id`),UNIQUE KEY `uk_user_account` (`account`),UNIQUE KEY `uk_user_email` (`email`)) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;
